class Solution {
    fun missingInteger(nums: IntArray): Int {
        var longestSeqLength = 1
        var sum = nums[0]

        // Find the sum of the longest sequential prefix
        for (i in 1 until nums.size) {
            if (nums[i] == nums[i - 1] + 1) {
                longestSeqLength++
                sum += nums[i]
            } else if (nums[i] <= nums[i - 1]) {
                break
            }
        }

        // Starting from sum, find the smallest missing integer greater than or equal to the sum
        var x = sum
        while (x in nums) {
            x++
        }

        return x
    }
}
