class Solution {
    fun missingInteger(nums: IntArray): Int {
        nums.sort() // Sort the array to identify the sequential prefix easily
        var sum = nums[0]
        var currentMax = nums[0]

        for (i in 1 until nums.size) {
            // Check if the current number continues the sequence
            if (nums[i] == currentMax + 1) {
                sum += nums[i]
                currentMax = nums[i]
            } else if (nums[i] > currentMax + 1) {
                // If we encounter a number greater than the next expected in sequence, break
                break
            }
        }

        // The smallest integer greater than or equal to the sum should be considered next
        // However, if the sum itself is not in the array and is greater than the last number of the longest sequential prefix, it should be the answer
        // Otherwise, the answer is sum + 1, ensuring it's not in the nums and greater than the sum of the sequential prefix
        var missingInteger = sum
        while (missingInteger in nums) {
            missingInteger++
        }

        return missingInteger
    }
}
