object Solution {
    def missingInteger(nums: Array[Int]): Int = {
        if (nums.isEmpty) return 1

        // Sort the array to ensure sequential order
        val sortedNums = nums.sorted

        var currentSum = sortedNums(0)
        var maxSum = currentSum

        // Iterate through the sorted array to find the longest sequential prefix
        for (i <- 1 until sortedNums.length) {
            if (sortedNums(i) == sortedNums(i - 1) + 1) {
                // Update current sum for sequential numbers
                currentSum += sortedNums(i)
            } else if (sortedNums(i) > sortedNums(i - 1) + 1) {
                // Found a break in the sequence, compare with maxSum
                maxSum = Math.max(maxSum, currentSum)
                // Reset currentSum to the new starting number of the next sequence
                currentSum = sortedNums(i)
            }
        }

        // After the loop, compare the last sequence's sum with maxSum
        maxSum = Math.max(maxSum, currentSum)

        // Starting from maxSum + 1, find the smallest missing integer
        var missingInt = maxSum + 1
        while (sortedNums.contains(missingInt)) {
            missingInt += 1
        }

        missingInt
    }
}
