object Solution {
  def missingInteger(nums: Array[Int]): Int = {
    // Sort the nums array to handle sequences correctly
    val sortedNums = nums.sorted.distinct

    // Initialize variables to track the longest sequential prefix
    var currentSum = 0
    var i = 0

    // Loop to find the longest sequential prefix
    while (i < sortedNums.length - 1 && sortedNums(i) + 1 == sortedNums(i + 1)) {
      currentSum += sortedNums(i)
      i += 1
    }
    // Add the last element of the longest sequential prefix to currentSum
    if (i < sortedNums.length) {
      currentSum += sortedNums(i)
    }

    // Find the smallest missing integer greater than or equal to the sum of the longest sequential prefix
    var missingInteger = currentSum + 1
    while (nums.contains(missingInteger)) {
      missingInteger += 1
    }

    missingInteger
  }
}
