#include <stdio.h>
#include <stdlib.h>
#include <string.h>

/**
 * Note: The returned array must be malloced, assume caller calls free().
 */
char **splitWordsBySeparator(char **words, int wordsSize, char separator, int *returnSize) {
    // Assuming a worst-case scenario where every character could be a potential split,
    // allocate enough space to accommodate all possible splits.
    char **result = (char **)malloc(wordsSize * 20 * sizeof(char *)); // Each word can at most produce 20 splits
    *returnSize = 0;

    for (int i = 0; i < wordsSize; i++) {
        char *word = words[i];
        int wordLen = strlen(word);
        char *temp = (char *)malloc((wordLen + 1) * sizeof(char));
        int tempIndex = 0;

        for (int j = 0; j <= wordLen; j++) {
            if (word[j] == separator || word[j] == '\0') {
                if (tempIndex != 0) { // Avoid adding empty strings
                    temp[tempIndex] = '\0'; // Null-terminate the current string
                    result[*returnSize] = (char *)malloc((tempIndex + 1) * sizeof(char));
                    strcpy(result[*returnSize], temp);
                    (*returnSize)++;
                    tempIndex = 0; // Reset tempIndex for the next word
                }
            } else {
                temp[tempIndex++] = word[j];
            }
        }
        free(temp);
    }

    // Optionally, reallocate the result to fit the actual size
    result = (char **)realloc(result, (*returnSize) * sizeof(char *));
    return result;
}
