import java.util.ArrayList;
import java.util.List;

class Solution {
    public List<String> splitWordsBySeparator(List<String> words, char separator) {
        List<String> result = new ArrayList<>();
        // Convert the separator to a String, escaping if it's a special character in regex
        String regexSeparator = escapeSeparator(separator);
        for (String word : words) {
            // Split the word by the escaped separator
            String[] splitWords = word.split(regexSeparator);
            // Iterate through the split words and add non-empty strings to the result
            for (String splitWord : splitWords) {
                if (!splitWord.isEmpty()) {
                    result.add(splitWord);
                }
            }
        }
        return result;
    }

    // Helper method to escape special characters if the separator is one of them
    private String escapeSeparator(char separator) {
        // List of special characters that need to be escaped in a regex
        String specialCharacters = ".|$#@";
        if (specialCharacters.indexOf(separator) >= 0) {
            // If the separator is a special character, escape it
            return "\\" + separator;
        } else {
            // If not a special character, return it as is
            return String.valueOf(separator);
        }
    }
}
