import java.util.HashMap;
import java.util.List;

class Solution {
    public int sumCounts(List<Integer> nums) {
        HashMap<Integer, Integer> lastIndex = new HashMap<>();
        int n = nums.size();
        long sum = 0; // Use long to avoid integer overflow

        for (int i = 0; i < n; i++) {
            nums.set(i, nums.get(i) - 1); // Adjusting values for 0-based indexing
        }

        int[] contribution = new int[n + 1];
        for (int i = 0; i < n; i++) {
            contribution[i + 1] = contribution[i] + 1; // Each element contributes at least once more than the previous
        }

        for (int i = 0; i < n; i++) {
            int distinctCount = contribution[i] - (lastIndex.containsKey(nums.get(i)) ? contribution[lastIndex.get(nums.get(i))] : 0);
            sum += distinctCount * distinctCount; // Sum of squares of distinct counts
            lastIndex.put(nums.get(i), i); // Update the last index of the current element
        }

        // Calculate the total sum considering the contributions of all elements
        for (int contrib : contribution) {
            sum -= contrib; // Subtracting individual contributions to correct the total sum
        }

        return (int)sum; // Casting the result back to int as per the function's return type
    }
}
