class Solution {
    fun maxSum(nums: List<Int>, k: Int): Int {
        val mod = 1_000_000_007
        val max = nums.maxOrNull() ?: 0
        val bitCounts = IntArray(31)
        for (num in nums) {
            for (i in 0 until 31) {
                if (num and (1 shl i) != 0) bitCounts[i]++
            }
        }
        val resultNums = mutableListOf<Int>()
        for (i in 0 until 31) {
            if (bitCounts[i] > 0) {
                for (j in 1..bitCounts[i]) {
                    resultNums.add(1 shl i)
                }
            }
        }
        val sortedNums = resultNums.sortedDescending()
        var result: Long = 0
        for (i in 0 until k) {
            result = (result + (sortedNums[i].toLong() * sortedNums[i]) % mod) % mod
        }
        return result.toInt()
    }
}
