class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @return Integer
     */
    function maxSum($nums, $k) {
        $mod = 1000000007;
        $maxVal = max($nums);
        $bitCount = array_fill(0, 31, 0);

        foreach ($nums as $num) {
            for ($i = 0; $i < 31; $i++) {
                if (($num >> $i) & 1) {
                    $bitCount[$i]++;
                }
            }
        }

        $result = 0;
        for ($i = 30; $i >= 0; $i--) {
            if ($bitCount[$i] >= $k) {
                $result += pow(2, $i) * $k;
                $result %= $mod;
                break;
            } else {
                if ($bitCount[$i] > 0) {
                    $result += pow(2, $i) * $bitCount[$i];
                    $k -= $bitCount[$i];
                }
                $result %= $mod;
            }
        }

        return $result;
    }
}
