impl Solution {
    pub fn max_sum(nums: Vec<i32>, k: i32) -> i32 {
        let mod_val = 1_000_000_007;
        let mut max_value = *nums.iter().max().unwrap();
        let mut bit_counts = vec![0; 31];

        // Count the number of elements with each bit set
        for num in &nums {
            for i in 0..31 {
                if (num & (1 << i)) != 0 {
                    bit_counts[i] += 1;
                }
            }
        }

        let mut dp = vec![vec![0; k as usize + 1]; 31];
        dp[0][0] = 1;
        for i in 0..31 {
            for j in 0..=k as usize {
                dp[i + 1][j] = dp[i][j]; // not using the i-th bit
                if bit_counts[i] > 0 && j >= 1 {
                    dp[i + 1][j] = (dp[i + 1][j] + dp[i][j - 1] * bit_counts[i] as i64) % mod_val;
                }
            }
        }

        let mut result = 0;
        for i in 0..31 {
            if bit_counts[i] > 0 {
                let mut temp = 0;
                for j in 1..=k as usize {
                    temp = (temp + dp[i][j - 1] * bit_counts[i] as i64) % mod_val;
                }
                result = (result + ((1 << i) * temp) % mod_val) % mod_val;
            }
        }

        result as i32
    }
}
