#include <vector>
#include <algorithm>
#include <unordered_map>

using namespace std;

class Solution {
public:
    static const int MOD = 1e9 + 7;

    int countPrimeFactors(int n) {
        unordered_map<int, int> primeFactors;
        for (int i = 2; i * i <= n; ++i) {
            while (n % i == 0) {
                primeFactors[i]++;
                n /= i;
            }
        }
        if (n > 1) primeFactors[n]++;
        return primeFactors.size();
    }

    int maximumScore(vector<int>& nums, int k) {
        vector<pair<int, int>> primeScores;
        for (int num : nums) {
            primeScores.push_back({countPrimeFactors(num), num});
        }
        sort(primeScores.begin(), primeScores.end(), [](const pair<int, int>& a, const pair<int, int>& b) {
            if (a.first == b.first) return a.second > b.second;
            return a.first > b.first;
        });

        long long result = 1;
        for (int i = 0; i < k && i < primeScores.size(); ++i) {
            result = (result * primeScores[i].second) % MOD;
        }
        return result;
    }
};
