class Solution {
    fun maximumScore(nums: List<Int>, k: Int): Int {
        val mod = 1000000007
        var maxScore = 1L

        val primeScores = nums.map { n -> n to primeScore(n) }
        val sortedNums = primeScores.sortedByDescending { it.second }.take(k)

        for (num in sortedNums) {
            maxScore = maxScore * num.first % mod
        }

        return maxScore.toInt()
    }

    private fun primeScore(n: Int): Int {
        var count = 0
        var number = n

        var factor = 2
        while (factor * factor <= number) {
            if (number % factor == 0) {
                count++
                while (number % factor == 0) {
                    number /= factor
                }
            }
            factor++
        }

        if (number > 1) count++

        return count
    }
}
