class Solution {
    fun maximumScore(nums: List<Int>, k: Int): Int {
        val mod = 1000000007
        val n = nums.size
        var maxScore = 1L
        val score = Array(n) { 0 }
        for (i in nums.indices) {
            score[i] = primeScore(nums[i])
        }
        val sortedIndices = score.indices.sortedWith(compareByDescending<Int> { score[it] }.thenBy { nums[it] })
        val used = BooleanArray(n) { false }
        for (i in 0 until k) {
            val index = sortedIndices[i]
            if (!used[index]) {
                maxScore = maxScore * nums[index] % mod
                used[index] = true
            }
        }
        return maxScore.toInt()
    }

    private fun primeScore(x: Int): Int {
        var n = x
        var factors = 0
        if (n % 2 == 0) {
            factors++
            while (n % 2 == 0) n /= 2
        }
        var i = 3
        while (i <= Math.sqrt(n.toDouble()).toInt()) {
            if (n % i == 0) {
                factors++
                while (n % i == 0) n /= i
            }
            i += 2
        }
        if (n > 1) factors++
        return factors
    }
}
