class Solution {
    func maximumScore(_ nums: [Int], _ k: Int) -> Int {
        let mod = 1_000_000_007
        var maxScore = 1
        var sortedNums = nums.enumerated().map { ($0.element, $0.offset) }
        sortedNums.sort { $0.0 > $1.0 }

        var used = Array(repeating: false, count: nums.count)
        var operations = 0

        for i in 0..<sortedNums.count {
            if operations == k { break }
            let (num, index) = sortedNums[i]
            if !used[index] {
                maxScore = (maxScore * num) % mod
                used[index] = true
                operations += 1

                // Attempt to expand to adjacent elements if not already used
                var left = index - 1
                var right = index + 1
                while left >= 0 && !used[left] && operations < k {
                    maxScore = (maxScore * nums[left]) % mod
                    used[left] = true
                    operations += 1
                    left -= 1
                }
                while right < nums.count && !used[right] && operations < k {
                    maxScore = (maxScore * nums[right]) % mod
                    used[right] = true
                    operations += 1
                    right += 1
                }
            }
        }

        return maxScore
    }
}
