public class Solution {
    public long BeautifulSubstrings(string s, int k) {
        long count = 0;
        for (int len = 2; len <= s.Length; len += 2) {
            int vowels = 0, consonants = 0, balance = 0;
            var freq = new int[2 * k];
            freq[k] = 1;

            for (int i = 0; i < s.Length; i++) {
                if (IsVowel(s[i])) vowels++;
                else consonants++;

                balance = (vowels - consonants) % k;
                if (balance < 0) balance += k;

                if (i >= len) {
                    if (IsVowel(s[i - len])) vowels--;
                    else consonants--;
                }

                if (i >= len - 1) {
                    count += freq[balance + k];
                }

                freq[balance + k]++;
            }
        }
        return count;
    }

    private bool IsVowel(char c) {
        return "aeiou".Contains(c);
    }
}
