#include <string>
#include <unordered_map>

class Solution {
public:
    long long beautifulSubstrings(string s, int k) {
        long long count = 0;
        int n = s.length();
        // Map to store difference between vowels and consonants count and its occurrences
        std::unordered_map<int, int> diffMap;
        int diff = 0; // Difference between vowels and consonants
        diffMap[0] = 1; // Initial condition

        for (int i = 0; i < n; ++i) {
            // Increment or decrement diff based on whether it's a vowel or consonant
            diff += (s[i] == 'a' || s[i] == 'e' || s[i] == 'i' || s[i] == 'o' || s[i] == 'u') ? 1 : -1;

            for (int j = 0; j*j <= diff * diff; ++j) {
                // Only check the cases where the product can be divisible by k
                if (j * (diff - j) % k == 0 || j * (diff + j) % k == 0) {
                    // Add count of substrings ending at current position with difference j or -j
                    count += diffMap[j - diff] + diffMap[-j - diff];
                }
            }
            // Increment the count of current difference for future substrings
            diffMap[diff]++;
        }

        return count;
    }
};
