#include <string>
#include <unordered_map>
#include <vector>

class Solution {
public:
    long long beautifulSubstrings(string s, int k) {
        long long result = 0;
        int n = s.length();
        std::unordered_map<int, int> count;
        count[0] = 1; // Base case: diff 0 occurs once before any characters are processed
        int vowels = 0, consonants = 0;

        for (int i = 0; i < n; ++i) {
            // Increment vowels or consonants count
            if (s[i] == 'a' || s[i] == 'e' || s[i] == 'i' || s[i] == 'o' || s[i] == 'u') {
                ++vowels;
            } else {
                ++consonants;
            }

            // Check for every possible (vowels - consonants) value
            for (int j = -n; j <= n; ++j) {
                int diff = vowels - consonants;
                if ((j == diff) && ((vowels * consonants) % k == 0) && vowels == consonants) {
                    result += count[j];
                }
            }
            count[vowels - consonants]++;
        }

        return result;
    }
};
