public class Solution {
    public int CountCompleteSubstrings(string word, int k) {
        int total = 0;
        for (char c = 'a'; c <= 'z'; ++c) {
            int count = 0;
            int[] freq = new int[26];
            for (int i = 0, j = 0; j < word.Length; ++j) {
                if (word[j] == c) ++count;
                ++freq[word[j] - 'a'];
                while (i <= j && (count > k || freq[word[j] - 'a'] > k)) {
                    if (word[i] == c) --count;
                    --freq[word[i++] - 'a'];
                }
                if (count == k && IsValid(freq, k)) total++;
            }
        }
        return total;
    }

    private bool IsValid(int[] freq, int k) {
        int minIndex = 25, maxIndex = 0;
        for (int i = 0; i < 26; i++) {
            if (freq[i] == k) {
                minIndex = Math.Min(minIndex, i);
                maxIndex = Math.Max(maxIndex, i);
            } else if (freq[i] != 0) return false;
        }
        return maxIndex - minIndex <= 2;
    }
}
