#include <string>
#include <vector>
#include <unordered_map>

using namespace std;

class Solution {
public:
    int countCompleteSubstrings(string word, int k) {
        int count = 0;
        for (int start = 0; start < word.length(); ++start) {
            unordered_map<char, int> charCount;
            vector<int> lastPos(26, -1);
            bool isValid = true;
            for (int end = start; end < word.length(); ++end) {
                char currentChar = word[end];
                ++charCount[currentChar];
                if (lastPos[currentChar - 'a'] != -1 && end - lastPos[currentChar - 'a'] > k) {
                    isValid = false;
                    break;
                }
                lastPos[currentChar - 'a'] = end;
                if (charCount[currentChar] > k) {
                    isValid = false;
                    break;
                }
                bool allK = true;
                for (auto &p : charCount) {
                    if (p.second != k) {
                        allK = false;
                        break;
                    }
                    if (end > start) {
                        char prevChar = word[end - 1];
                        if (abs(prevChar - currentChar) > 2) {
                            allK = false;
                            break;
                        }
                    }
                }
                if (allK && isValid) {
                    ++count;
                }
            }
        }
        return count;
    }
};
