#include <string>
#include <vector>
#include <unordered_map>

using namespace std;

class Solution {
public:
    int countCompleteSubstrings(string word, int k) {
        int result = 0;
        for (int start = 0; start < word.size(); ++start) {
            unordered_map<char, int> freq;
            bool invalid = false;
            for (int end = start; end < word.size() && !invalid; ++end) {
                freq[word[end]]++;
                // Check if all characters meet the frequency requirement and adjacent difference condition
                bool valid = true;
                int prevFreq = -1;
                for (auto &pair : freq) {
                    if (pair.second > k) {
                        invalid = true;
                        break;
                    }
                    if (prevFreq != -1 && pair.second != prevFreq) {
                        valid = false;
                    }
                    prevFreq = pair.second;
                }
                if (valid && !invalid) {
                    for (int i = start; i <= end; ++i) {
                        for (int j = i + 1; j <= end; ++j) {
                            if (abs(word[i] - word[j]) > 2) {
                                valid = false;
                                break;
                            }
                        }
                        if (!valid) break;
                    }
                }
                if (valid && !invalid && prevFreq == k) result++;
            }
        }
        return result;
    }
};
