#include <string.h>
#include <stdlib.h>
#include <stdbool.h>

int countCompleteSubstrings(char* word, int k) {
    int count = 0;
    int len = strlen(word);
    int freq[26] = {0};
    for (int start = 0; start < len; start++) {
        memset(freq, 0, sizeof(freq));
        for (int end = start; end < len; end++) {
            int idx = word[end] - 'a';
            freq[idx]++;
            bool isValid = true;
            int prevFreq = 0;
            for (int i = 0; i < 26; i++) {
                if (freq[i] == 0) continue;
                if (freq[i] != k) {
                    isValid = false;
                    break;
                }
                if (prevFreq > 0 && (abs(prevFreq - i) > 2)) {
                    isValid = false;
                    break;
                }
                prevFreq = i;
            }
            if (isValid) count++;
        }
    }
    return count;
}
