class Solution {
  int countCompleteSubstrings(String word, int k) {
    int result = 0;
    for (int first = 0; first < 26; ++first) {
      Map<int, int> freq = {};
      int uniqueCount = 0;
      int kCount = 0;
      for (int start = 0, end = 0; end < word.length; end++) {
        int endChar = word.codeUnitAt(end) - 'a'.codeUnitAt(0);
        freq[endChar] = (freq[endChar] ?? 0) + 1;
        if (freq[endChar] == 1) uniqueCount++;
        if (freq[endChar] == k) kCount++;

        while (uniqueCount > 3) {
          int startChar = word.codeUnitAt(start) - 'a'.codeUnitAt(0);
          if (freq[startChar] == k) kCount--;
          freq[startChar] = freq[startChar]! - 1;
          if (freq[startChar] == 0) uniqueCount--;
          start++;
        }

        if (uniqueCount == kCount && uniqueCount <= 3 && isAdjacent(first, freq.keys.toList())) {
          result++;
        }
      }
    }
    return result;
  }

  bool isAdjacent(int first, List<int> keys) {
    for (int key in keys) {
      if (key < first || key > first + 2) return false;
    }
    return true;
  }
}
