-spec count_complete_substrings(Word :: unicode:unicode_binary(), K :: integer()) -> integer().
count_complete_substrings(Word, K) ->
    S = unicode:characters_to_list(Word),
    ValidSubstrings = lists:foldl(fun(Char, Acc) ->
        {NewAcc, _} = lists:foldl(fun
            (C, {SubAcc, SubStr}) when C =:= Char; C =:= Char-1; C =:= Char+1 ->
                case maps:get(C, SubStr, 0) of
                    Count when Count < K -> {SubAcc, maps:put(C, Count+1, SubStr)};
                    Count when Count == K -> {SubAcc + 1, maps:remove(C, SubStr)}
                end;
            (_, {SubAcc, SubStr}) ->
                {SubAcc, SubStr}
            end, {0, #{}}, S),
        Acc + NewAcc
    end, 0, lists:seq($a, $z)),
    ValidSubstrings.
