class Solution {
    public int countCompleteSubstrings(String word, int k) {
        int totalSubstrings = 0;
        for (char start = 'a'; start <= 'z'; start++) {
            for (char end = start; end <= 'z' && end <= start + 2; end++) {
                int[] freq = new int[26];
                int count = 0;
                for (int left = 0, right = 0; right < word.length(); right++) {
                    if (word.charAt(right) < start || word.charAt(right) > end) {
                        while (left <= right) {
                            freq[word.charAt(left) - 'a'] = 0;
                            left++;
                        }
                        count = 0;
                        continue;
                    }
                    freq[word.charAt(right) - 'a']++;
                    if (freq[word.charAt(right) - 'a'] == k) count++;
                    if (freq[word.charAt(right) - 'a'] == k + 1) count--;
                    if (count == (end - start + 1)) {
                        totalSubstrings++;
                        while (left < word.length() && word.charAt(left) >= start && word.charAt(left) <= end) {
                            if (freq[word.charAt(left) - 'a'] == k) count--;
                            freq[word.charAt(left) - 'a']--;
                            if (freq[word.charAt(left) - 'a'] == k - 1) count++;
                            left++;
                            if (count == (end - start + 1)) totalSubstrings++;
                            else break;
                        }
                    }
                }
            }
        }
        return totalSubstrings;
    }
}
