class Solution {
    public int countCompleteSubstrings(String word, int k) {
        int total = 0;
        for (int start = 0; start < word.length(); start++) {
            int[] freq = new int[26];
            int maxChar = 0, uniqueKCount = 0;
            for (int end = start; end < word.length(); end++) {
                int charIndex = word.charAt(end) - 'a';
                if (++freq[charIndex] == k) uniqueKCount++;
                maxChar = Math.max(maxChar, charIndex);
                if (freq[charIndex] > k || (maxChar - (start == end ? maxChar : word.charAt(start) - 'a') > 2)) {
                    if (freq[charIndex] == k + 1) uniqueKCount--;
                    break;
                }
                if (uniqueKCount == end - start + 1) total++;
            }
        }
        return total;
    }
}
