class Solution {
    public int countCompleteSubstrings(String word, int k) {
        int res = 0;
        for (char c = 'a'; c <= 'z'; ++c) {
            res += countSubstringsWithCharKTimes(word, c, k);
        }
        return res;
    }

    private int countSubstringsWithCharKTimes(String s, char c, int k) {
        int res = 0, freq = 0, validFreq = 0, l = 0;
        int[] count = new int[128];
        for (int r = 0; r < s.length(); ++r) {
            if (s.charAt(r) == c) {
                ++freq;
                if (freq <= k) ++validFreq;
                if (freq == k + 1) --validFreq;
            }
            count[s.charAt(r)]++;
            while (count[s.charAt(r)] > k || (r - l + 1) > k * 3) {
                if (s.charAt(l) == c) {
                    --freq;
                    if (freq < k) --validFreq;
                }
                count[s.charAt(l++)]--;
            }
            if (validFreq == k) res += l + 1;
        }
        return res;
    }
}
