class Solution {
    fun countCompleteSubstrings(word: String, k: Int): Int {
        val n = word.length
        var count = 0

        for (start in 0 until n) {
            val freq = IntArray(26) { 0 }
            var uniqueChars = 0
            for (end in start until n) {
                if (++freq[word[end] - 'a'] == 1) uniqueChars++
                if (freq[word[end] - 'a'] == k) uniqueChars--

                if (uniqueChars == 0 && isAdjacentValid(freq, k)) count++
            }
        }

        return count
    }

    private fun isAdjacentValid(freq: IntArray, k: Int): Boolean {
        var prev = -3 // Start with a non-adjacent value
        freq.forEachIndexed { index, count ->
            if (count > 0) {
                if (count != k) return false // Early exit if any char doesn't meet the exact frequency
                if (prev >= 0 && index - prev > 2) return false // Check adjacency with a gap of more than 2
                prev = index
            }
        }
        return true
    }
}
