class Solution {

    /**
     * @param String $word
     * @param Integer $k
     * @return Integer
     */
    function countCompleteSubstrings($word, $k) {
        $result = 0;
        $n = strlen($word);

        for ($start = 0; $start < $n; $start++) {
            $freq = array_fill(0, 26, 0);
            for ($end = $start; $end < $n; $end++) {
                $freq[ord($word[$end]) - ord('a')]++;
                if ($freq[ord($word[$end]) - ord('a')] > $k) break;

                $allK = true;
                $prevCharCount = -1;
                for ($i = 0; $i < 26; $i++) {
                    if ($freq[$i] == 0) continue;
                    if ($freq[$i] != $k) {
                        $allK = false;
                        break;
                    }
                    if ($prevCharCount != -1 && ($i - $prevCharCount > 2)) {
                        $allK = false;
                        break;
                    }
                    $prevCharCount = $i;
                }

                if ($allK) $result++;
            }
        }

        return $result;
    }
}
