class Solution {

    /**
     * @param String $word
     * @param Integer $k
     * @return Integer
     */
    function countCompleteSubstrings($word, $k) {
        $res = 0;
        for ($i = 0; $i < strlen($word); ++$i) {
            $count = [];
            for ($j = $i; $j < strlen($word); ++$j) {
                if (!isset($count[$word[$j]])) {
                    $count[$word[$j]] = 1;
                } else {
                    $count[$word[$j]]++;
                }

                if ($count[$word[$j]] > $k) break;

                $valid = true;
                foreach ($count as $char => $cnt) {
                    if ($cnt !== $k) {
                        $valid = false;
                        break;
                    }
                }

                if ($valid && $this->checkAdjacentChars(array_keys($count))) {
                    $res++;
                }
            }
        }
        return $res;
    }

    private function checkAdjacentChars($chars) {
        sort($chars);
        for ($i = 1; $i < count($chars); $i++) {
            if (ord($chars[$i]) - ord($chars[$i - 1]) > 2) {
                return false;
            }
        }
        return true;
    }
}
