impl Solution {
    pub fn count_complete_substrings(word: String, k: i32) -> i32 {
        let mut count = 0;
        let k = k as usize;
        let word = word.as_bytes();
        let n = word.len();

        'outer: for start in 0..n {
            let mut freq = vec![0; 26];
            for end in start..n {
                let idx = (word[end] - b'a') as usize;
                freq[idx] += 1;
                if freq[idx] > k {
                    continue 'outer;
                }
                if end - start + 1 >= k * 26 {
                    let mut valid = true;
                    let mut prev_char = 0;
                    for i in 0..26 {
                        if freq[i] > 0 {
                            if freq[i] != k {
                                valid = false;
                                break;
                            }
                            if prev_char != 0 && (i as i32 - prev_char).abs() > 2 {
                                valid = false;
                                break;
                            }
                            prev_char = i as i32;
                        }
                    }
                    if valid {
                        count += 1;
                    }
                }
            }
        }

        count
    }
}
