impl Solution {
    pub fn count_complete_substrings(word: String, k: i32) -> i32 {
        let mut result = 0;
        let word_len = word.len();
        let word = word.as_bytes();
        let k = k as usize;

        for start in 0..word_len {
            let mut freq = vec![0; 26];
            let mut unique_chars = 0;
            for end in start..word_len {
                let idx = (word[end] - b'a') as usize;
                if freq[idx] == 0 {
                    unique_chars += 1;
                }
                freq[idx] += 1;

                if freq[idx] > k {
                    break;
                }

                if freq[idx] == k && unique_chars * k == end - start + 1 {
                    let mut max_diff = 0;
                    let mut last_char = 0;
                    let mut first = true;
                    for (i, &f) in freq.iter().enumerate() {
                        if f == k {
                            if first {
                                last_char = i;
                                first = false;
                                continue;
                            }
                            max_diff = max_diff.max(i - last_char);
                            last_char = i;
                        }
                    }
                    if max_diff <= 2 {
                        result += 1;
                    }
                }
            }
        }

        result
    }
}
