object Solution {
    def countCompleteSubstrings(word: String, k: Int): Int = {
        val n = word.length
        var count = 0

        for (start <- 0 until n) {
            val freq = new Array[Int](26)
            var uniqueChars = 0
            var validChars = 0

            for (end <- start until n) {
                val index = word(end) - 'a'
                if (freq(index) == 0) uniqueChars += 1
                freq(index) += 1
                if (freq(index) == k) validChars += 1

                if (uniqueChars == validChars && checkAdjacent(freq, k)) count += 1
            }
        }

        count
    }

    def checkAdjacent(freq: Array[Int], k: Int): Boolean = {
        var prev = -3 // Initialize with a value that is out of possible range
        for (i <- freq.indices) {
            if (freq(i) > 0 && freq(i) != k) return false
            if (freq(i) == k) {
                if (prev >= 0 && (i - prev) > 2) return false
                prev = i
            }
        }
        true
    }
}
