object Solution {
    def countCompleteSubstrings(word: String, k: Int): Int = {
        var total = 0
        for (i <- 'a' to 'z') {
            for (j <- i to ('z'.min((i + 2).toChar))) {
                var count = Array.fill(26)(0)
                var unique = 0
                var complete = 0
                var left = 0
                for (right <- word.indices) {
                    if (word(right) < i || word(right) > j) {
                        count = Array.fill(26)(0)
                        unique = 0
                        complete = 0
                        left = right + 1
                    } else {
                        val rIndex = word(right) - 'a'
                        if (count(rIndex) == 0) unique += 1
                        count(rIndex) += 1
                        if (count(rIndex) == k) complete += 1
                        if (count(rIndex) == k + 1) complete -= 1
                        if (unique == complete && unique > 0) total += 1
                    }
                }
            }
        }
        total
    }
}
