class Solution {
    func countCompleteSubstrings(_ word: String, _ k: Int) -> Int {
        let chars = Array(word)
        var count = 0

        for start in 0..<chars.count {
            var freq = [Character: Int]()
            for end in start..<chars.count {
                freq[chars[end], default: 0] += 1
                if freq.values.allSatisfy({ $0 == k }) && isAdjacentDiffAtMostTwo(Array(freq.keys)) {
                    count += 1
                }
            }
        }

        return count
    }

    private func isAdjacentDiffAtMostTwo(_ chars: [Character]) -> Bool {
        for i in 1..<chars.count {
            if abs(Int(chars[i].asciiValue!) - Int(chars[i-1].asciiValue!)) > 2 {
                return false
            }
        }
        return true
    }
}
