class Solution {
    func countCompleteSubstrings(_ word: String, _ k: Int) -> Int {
        var result = 0
        let n = word.count
        let s = Array(word.utf8).map { Int($0) - 97 }

        for i in 0..<26 {
            var count = [Int](repeating: 0, count: n + 1)
            for j in 0..<n {
                count[j + 1] = count[j] + (s[j] == i ? 1 : 0)
            }
            for start in 0..<n {
                for end in start + 1...n where (end - start) % k == 0 {
                    let uniqueChars = Set(s[start..<end]).count
                    if uniqueChars == 1 || (uniqueChars == 2 && abs(s[start] - s[end - 1]) == 2) {
                        if count[end] - count[start] == (end - start) / k {
                            result += 1
                        }
                    }
                }
            }
        }
        return result
    }
}
