function countCompleteSubstrings(word: string, k: number): number {
    let total = 0;
    // Iterate through all possible 26 characters as starting points
    for (let i = 0; i < 26; i++) {
        let freq = Array(26).fill(0);
        let count = 0;
        let distinctChar = 0;
        for (let start = 0, end = 0; end < word.length; end++) {
            // Update frequency of the current character
            let index = word.charCodeAt(end) - 'a'.charCodeAt(0);
            if (freq[index] == 0) distinctChar++;
            freq[index]++;
            // If the character's frequency hits k, increase count
            if (freq[index] == k) count++;
            // While the number of distinct characters is greater than the allowed range (i + 1 to i + 3)
            while (distinctChar > 3 || (distinctChar == 3 && (i + 2 < index || freq[i] == 0))) {
                let startIdx = word.charCodeAt(start) - 'a'.charCodeAt(0);
                if (freq[startIdx] == k) count--;
                freq[startIdx]--;
                if (freq[startIdx] == 0) distinctChar--;
                start++;
            }
            // If all characters in the window have exactly k occurrences, increment total
            if (count == distinctChar && distinctChar <= 3) {
                total += 1;
            }
        }
    }
    return total;
}
