function countCompleteSubstrings(word: string, k: number): number {
    let result = 0;
    const n = word.length;

    // Function to check if all characters in the map have exactly k occurrences
    const allCharsHaveKOccurrences = (charMap: Map<string, number>): boolean => {
        for (let value of charMap.values()) {
            if (value !== k) return false;
        }
        return true;
    };

    for (let start = 0; start < n; start++) {
        const charMap = new Map<string, number>();
        for (let end = start; end < n; end++) {
            const char = word[end];
            charMap.set(char, (charMap.get(char) || 0) + 1);

            // Check if current substring is valid
            if (charMap.size > 1 && allCharsHaveKOccurrences(charMap)) {
                let valid = true;
                let prevChar = '';
                for (let j = start; j <= end; j++) {
                    if (prevChar && Math.abs(word.charCodeAt(j) - word.charCodeAt(j - 1)) > 2) {
                        valid = false;
                        break;
                    }
                    prevChar = word[j];
                }
                if (valid) result++;
            }
        }
    }

    return result;
}
