#include <vector>
#include <string>
#include <algorithm>
#include <unordered_map>

using namespace std;

class Solution {
public:
    int countKSubsequencesWithMaxBeauty(string s, int k) {
        const int mod = 1e9 + 7;
        unordered_map<char, int> freq;
        for (char c : s) freq[c]++;

        vector<int> beauties;
        for (auto &[_, count] : freq) beauties.push_back(count);

        sort(beauties.rbegin(), beauties.rend());

        long long maxBeauty = 0;
        for (int i = 0; i < k; ++i) maxBeauty += beauties[i];

        vector<vector<long long>> dp(k + 1, vector<long long>(beauties.size() + 1, 0));
        dp[0][0] = 1;

        for (int i = 1; i <= beauties.size(); ++i) {
            for (int j = 0; j <= k; ++j) {
                dp[j][i] = dp[j][i - 1];
                if (j > 0) dp[j][i] = (dp[j][i] + dp[j - 1][i - 1] * beauties[i - 1]) % mod;
            }
        }

        long long count = 0;
        for (int i = 0; i <= beauties.size(); ++i) {
            if (i >= k && beauties[i - k] == beauties[k - 1]) {
                count = (count + dp[k][i]) % mod;
            }
        }

        return count;
    }
};
