#include <vector>
#include <string>
#include <algorithm>
#include <unordered_map>

using namespace std;

class Solution {
public:
    int countKSubsequencesWithMaxBeauty(string s, int k) {
        const int mod = 1e9 + 7;
        unordered_map<char, int> freq;
        for (char c : s) ++freq[c];

        // Collect frequencies in descending order
        vector<int> frequencies;
        for (const auto& p : freq) frequencies.push_back(p.second);
        sort(frequencies.rbegin(), frequencies.rend());

        // Calculate the maximum beauty
        long long maxBeauty = 0;
        for (int i = 0; i < k; ++i) maxBeauty += frequencies[i];

        // Combinatorial calculation
        vector<vector<long long>> comb(26, vector<long long>(k+1, 0));
        for (int i = 0; i < 26; ++i) {
            comb[i][0] = 1;
            for (int j = 1; j <= min(i, k); ++j)
                comb[i][j] = (comb[i-1][j] + comb[i-1][j-1]) % mod;
        }

        long long ans = 1;
        int extra = 0; // Count characters with the same frequency as the k-th
        for (int i = 0; i < frequencies.size(); ++i) {
            if (i < k) {
                int freq = frequencies[i];
                if (i < k-1 && freq == frequencies[i+1])
                    continue; // Skip until the last of the duplicates
                int count = count_if(frequencies.begin(), frequencies.end(),
                                     [freq](int x) { return x == freq; });
                ans = ans * comb[extra + count][count] % mod;
                extra += count - 1; // Adjust extra for already used
            } else if (frequencies[i] == frequencies[k-1]) {
                extra++; // Increment extra for characters with the same frequency as the k-th
            }
        }

        return ans;
    }
};
