#include <stdio.h>
#include <string.h>
#include <stdlib.h>

#define MOD 1000000007

// Function to calculate power with modulo
long long power(long long base, int exponent, int modulo) {
    long long result = 1;
    base = base % modulo;
    while (exponent > 0) {
        if (exponent & 1)
            result = (result * base) % modulo;
        exponent = exponent >> 1;
        base = (base * base) % modulo;
    }
    return result;
}

// Function to calculate factorial modulo mod
long long factorialMod(int n, int mod) {
    long long result = 1;
    for (int i = 1; i <= n; ++i) {
        result = (result * i) % mod;
    }
    return result;
}

// Function to calculate modular inverse using Fermat's little theorem
long long modInverse(long long n, int mod) {
    return power(n, mod - 2, mod);
}

// Main function to count k-subsequences with max beauty
int countKSubsequencesWithMaxBeauty(char* s, int k) {
    int freq[26] = {0};
    int n = strlen(s);

    // Count frequency of each character in s
    for (int i = 0; i < n; i++) {
        freq[s[i] - 'a']++;
    }

    // Sort frequencies in decreasing order
    for (int i = 0; i < 26 - 1; i++) {
        for (int j = 0; j < 26 - i - 1; j++) {
            if (freq[j] < freq[j + 1]) {
                int temp = freq[j];
                freq[j] = freq[j + 1];
                freq[j + 1] = temp;
            }
        }
    }

    // Find the maximum beauty and the number of characters with that frequency
    long long maxBeauty = 0, ways = 1;
    for (int i = 0; i < k; i++) {
        maxBeauty += freq[i];
    }

    // Count frequencies of each frequency value
    int freqCount[101] = {0}; // Assuming maximum frequency of a character in s does not exceed 100
    for (int i = 0; i < 26; i++) {
        if (freq[i] > 0) freqCount[freq[i]]++;
    }

    // Calculate combinations for each frequency level
    for (int i = 0; i < k && i < 26; i++) {
        if (freqCount[freq[i]] > 1) {
            int f = freqCount[freq[i]];
            long long numerator = factorialMod(f, MOD);
            long long denominator = (factorialMod(f - 1, MOD) * modInverse(k - i, MOD)) % MOD;
            ways = (ways * numerator * modInverse(denominator, MOD)) % MOD;
            freqCount[freq[i]]--;
        }
    }

    return (int)(ways % MOD);
}
