-spec count_k_subsequences_with_max_beauty(S :: unicode:unicode_binary(), K :: integer()) -> integer().
count_k_subsequences_with_max_beauty(S, K) ->
    Freqs = lists:foldl(fun(C, Acc) -> maps:update_with(C, fun(X) -> X + 1 end, 1, Acc) end, #{}, binary:bin_to_list(S)),
    Values = lists:sort(maps:values(Freqs)),
    MaxValues = lists:reverse(tl(lists:reverse(Values))),
    Comb = fun(N, K) when N >= K -> 
                  Prod = lists:foldl(fun(X, {P, I}) -> {P * X div I, I + 1} end, {1, 1}, lists:seq(N - K + 1, N)),
                  Prod;
               (_, _) -> 
                  {0, 0}
           end,
    Count = fun
        Count([], _, Acc) -> Acc;
        Count([H|T], K, Acc) when H >= K ->
            {C, _} = Comb(H, K),
            Count(T, K, (Acc + C) rem 1000000007);
        Count(_, _, Acc) -> Acc
    end,
    Count(MaxValues, K, 0).
