func countKSubsequencesWithMaxBeauty(s string, k int) int {
 const MOD int = 1e9 + 7
 freq := make(map[rune]int)
 for _, c := range s {
  freq[c]++
 }

 type pair struct {
  freq, count int
 }
 freqs := make([]pair, 0)
 for _, v := range freq {
  freqs = append(freqs, pair{v, 0})
 }

 for i := range freqs {
  freqs[i].count = 1
  for j := freqs[i].freq - 1; j > freqs[i].freq-k; j-- {
   freqs[i].count = (freqs[i].count * j) % MOD
  }
 }

 maxBeauty, ways := 0, 0
 for _, sub := range freqs {
  if sub.freq >= k {
   maxBeauty = max(maxBeauty, sub.freq)
  }
 }

 for _, sub := range freqs {
  if sub.freq == maxBeauty {
   ways = (ways + sub.count) % MOD
  }
 }

 return ways
}

func max(a, b int) int {
 if a > b {
  return a
 }
 return b
}
