class Solution {
    public int countKSubsequencesWithMaxBeauty(String s, int k) {
        final int MOD = 1000000007;
        int[] freq = new int[26];
        for (int i = 0; i < s.length(); i++) {
            freq[s.charAt(i) - 'a']++;
        }

        // Find the beauty of all possible k-subsequences
        long maxBeauty = 0;
        for (int i = 0; i < 26; i++) {
            if (freq[i] > 0) {
                maxBeauty += freq[i];
            }
        }

        // Count how many characters have the maximum frequency
        int maxFreq = 0;
        for (int f : freq) {
            if (f > maxFreq) {
                maxFreq = f;
            }
        }

        // Calculate the number of k-subsequences with maximum beauty
        long count = 1;
        for (int i = 0; i < 26; i++) {
            if (freq[i] == maxFreq) {
                count = (count * (freq[i] + 1)) % MOD;
            }
        }

        // Adjust the count for the k characters
        for (int i = 1; i <= k; i++) {
            count = (count * i) % MOD;
        }

        return (int) count;
    }
}
