class Solution {
    public int countKSubsequencesWithMaxBeauty(String s, int k) {
        final int MOD = 1000000007;
        int[] freq = new int[26];
        for (char c : s.toCharArray()) {
            freq[c - 'a']++;
        }

        // Sort frequencies in descending order
        Integer[] freqSorted = new Integer[26];
        for (int i = 0; i < 26; i++) {
            freqSorted[i] = freq[i];
        }
        Arrays.sort(freqSorted, Collections.reverseOrder());

        // Calculate the maximum beauty
        long maxBeauty = 0;
        for (int i = 0; i < k; i++) {
            maxBeauty += freqSorted[i];
        }

        // Count combinations for achieving max beauty
        long count = 1;
        for (int i = 0; i < k; i++) {
            int f = freqSorted[i];
            long combinations = 0;
            for (int j = 0; j < 26; j++) {
                if (freq[j] == f) {
                    combinations++;
                }
            }

            count = (count * combinations) % MOD;
            // Reduce frequency to avoid recounting the same character
            for (int j = 0; j < 26; j++) {
                if (freq[j] == f) {
                    freq[j]--;
                    break;
                }
            }
        }

        return (int) count;
    }
}
