class Solution {
    public int countKSubsequencesWithMaxBeauty(String s, int k) {
        final int MOD = 1000000007;
        int[] freq = new int[26];
        for (char c : s.toCharArray()) {
            freq[c - 'a']++;
        }

        long[][] dp = new long[k + 1][27];
        dp[0][26] = 1; // Base case: 0 characters chosen

        for (int i = 0; i < 26; i++) {
            for (int j = k; j >= 1; j--) {
                for (int l = 26; l >= 1; l--) {
                    if (freq[i] >= l) {
                        dp[j][l] = (dp[j][l] + dp[j - 1][l - 1] * freq[i]) % MOD;
                    }
                }
            }
        }

        long result = 0;
        for (int i = 1; i <= 26; i++) {
            result = (result + dp[k][i]) % MOD;
        }

        return (int) result;
    }
}
