/**
 * @param {string} s
 * @param {number} k
 * @return {number}
 */
var countKSubsequencesWithMaxBeauty = function(s, k) {
    const mod = 10**9 + 7;
    const freq = new Array(26).fill(0);
    for (let i = 0; i < s.length; i++) {
        freq[s.charCodeAt(i) - 'a'.charCodeAt(0)]++;
    }

    freq.sort((a, b) => b - a);
    let maxBeauty = freq.slice(0, k).reduce((a, b) => a + b, 0);
    let ways = 1;

    for (let i = 0; i < k; i++) {
        let count = 0;
        for (let j = 0; j < s.length; j++) {
            if (freq[i] === freq[s.charCodeAt(j) - 'a'.charCodeAt(0)]) count++;
        }
        ways = (ways * count) % mod;
    }

    return ways;
};
