/**
 * @param {string} s
 * @param {number} k
 * @return {number}
 */
var countKSubsequencesWithMaxBeauty = function(s, k) {
    const mod = 10**9 + 7;
    let freq = {};
    for (let char of s) {
        freq[char] = (freq[char] || 0) + 1;
    }

    let values = Object.values(freq).sort((a, b) => b - a);
    let maxBeautyValues = values.slice(0, k);
    let maxBeauty = maxBeautyValues.reduce((acc, val) => acc + val, 0);
    let counts = new Array(maxBeauty + 1).fill(0);
    for (let val of values) {
        counts[val] = (counts[val] || 0) + 1;
    }

    let comb = (n, r) => {
        let result = 1;
        for (let i = 1; i <= r; ++i) {
            result = (result * (n - i + 1)) / i;
        }
        return result;
    };

    let ways = 1;
    for (let val of maxBeautyValues) {
        ways = (ways * comb(counts[val], maxBeautyValues.filter(x => x === val).length)) % mod;
        counts[val] -= maxBeautyValues.filter(x => x === val).length;
    }

    return ways;
};
