 * @param {number} k
 * @return {number}
 */
var countKSubsequencesWithMaxBeauty = function(s, k) {
    const mod = 1e9 + 7;
    const freq = new Array(26).fill(0);
    for (const char of s) {
        freq[char.charCodeAt(0) - 'a'.charCodeAt(0)]++;
    }

    const combinations = (n, k) => {
        let result = 1;
        for (let i = 1; i <= k; ++i) {
            result = (result * (n - i + 1)) / i;
        }
        return result;
    }

    freq.sort((a, b) => b - a);
    const topKFreq = freq.slice(0, k);

    let maxBeauty = topKFreq.reduce((acc, val) => acc + val, 0);
    let ways = 1;
    for (let i = 0; i < k; ++i) {
        const freqCount = freq.filter(f => f === topKFreq[i]).length;
        ways = (ways * combinations(freqCount, freq.slice(0, k).filter(f => f === topKFreq[i]).length)) % mod;
    }

    return ways;
};
