class Solution {
    fun countKSubsequencesWithMaxBeauty(s: String, k: Int): Int {
        val mod = 1_000_000_007
        val freq = IntArray(26)
        for (char in s) {
            freq[char - 'a']++
        }
        freq.sortDescending()
        var maxBeauty = freq.take(k).sum()
        val freqCount = IntArray(s.length + 1)
        for (f in freq) {
            freqCount[f]++
        }
        var combinations = 1L
        for (i in 0 until k) {
            combinations = combinations * freqCount[freq[i]] % mod
            freqCount[freq[i]]--
        }
        return combinations.toInt()
    }
}
