class Solution {

    /**
     * @param String $s
     * @param Integer $k
     * @return Integer
     */
    function countKSubsequencesWithMaxBeauty($s, $k) {
        $mod = 1000000007;
        $freq = array_count_values(str_split($s));
        arsort($freq);
        $freq = array_values($freq);

        if ($k == 1) {
            return count($freq) % $mod;
        }

        $comb = function($n, $k) use ($mod) {
            $result = 1;
            for ($i = 0; $i < $k; $i++) {
                $result = ($result * ($n - $i)) % $mod;
                $result = ($result / ($i + 1)) % $mod;
            }
            return $result;
        };

        $maxBeauty = 0;
        for ($i = 0; $i < $k; $i++) {
            $maxBeauty += $freq[$i];
        }

        $countMaxBeauty = 0;
        foreach ($freq as $f) {
            if ($f == $freq[$k - 1]) {
                $countMaxBeauty++;
            }
        }

        return ($comb($countMaxBeauty, $k) % $mod);
    }
}
